
var startdiv=document.getElementById("startdiv");var maindiv=document.getElementById("maindiv");var label=document.getElementById("label");var planscore=document.getElementById("planscore");var enddiv=document.getElementById("enddiv");var tid;var scores=0;var enemys=[];var bullets=[];var time1=0;var time2=0;function begin(){startdiv.style.display="none";maindiv.style.display="block";tid=setInterval(start,30);}
function chongxin(){window.location.reload(true);}
function start(){time1++;if(time1==20){time2++;if(time2%5==0){enemys.push(new enemy(25,282,46,64,'image/enemy3_fly_1.png',3,"image/中飞机爆炸.gif",5,360,500));}
if(time2==20){enemys.push(new enemy(57,216,110,170,'image/enemy2_fly_1.png',1,"image/大飞机爆炸.gif",20,600,1000));time2=0;}
else{enemys.push(new enemy(5,285,34,24,'image/enemy1_fly_1.png',4,"image/小飞机爆炸.gif",1,300,1));}
time1=0;}
var enemylen=enemys.length;for(var i=0;i<enemylen;i++){if(enemys[i].planisdie==false){enemys[i].move();}
if(enemys[i].plannode.offsetTop>500){maindiv.removeChild(enemys[i].plannode);enemys.splice(i,1);enemylen--;}
if(enemys[i].planisdie==true){enemys[i].diecount+=20;if(enemys[i].diecount==enemys[i].dietime){maindiv.removeChild(enemys[i].plannode);enemys.splice(i,1);enemylen--;}}}
if(time1%5==0){bullets.push(new oddbullet((selfpaln.plannode.offsetLeft+31),(selfpaln.plannode.offsetTop)));}
var bulletlen=bullets.length;for(var i=0;i<bulletlen;i++){bullets[i].move();if(bullets[i].bulletnode.offsetTop<0){maindiv.removeChild(bullets[i].bulletnode);bullets.splice(i,1);bulletlen--;}}
for(var k=0;k<bulletlen;k++){for(var j=0;j<enemylen;j++){if(enemys[j].planisdie==false){if(enemys[j].plannode.offsetLeft<=selfpaln.plannode.offsetLeft+66&&enemys[j].plannode.offsetLeft+enemys[j].w>=selfpaln.plannode.offsetLeft){if(enemys[j].plannode.offsetTop<=selfpaln.plannode.offsetTop+80&&enemys[j].plannode.offsetTop+enemys[j].h>=selfpaln.plannode.offsetTop+20){selfpaln.plannode.src="image/本方飞机爆炸.gif";enddiv.style.display="block";planscore.innerHTML=scores;clearInterval(tid);if(document.removeEventListener){maindiv.removeEventListener("mousemove",ourmove,true);}else if(document.detachEvent){maindiv.detachEvent("onmousemove",ourmove);}}}}
if(bullets[k].bulletnode.offsetLeft<=enemys[j].plannode.offsetLeft+enemys[j].w&&bullets[k].bulletnode.offsetLeft+6>=enemys[j].plannode.offsetLeft){if(bullets[k].bulletnode.offsetTop<=enemys[j].plannode.offsetTop+enemys[j].h&&bullets[k].bulletnode.offsetTop+14>=enemys[j].plannode.offsetTop){enemys[j].hp-=1;if(enemys[j].hp==0){enemys[j].plannode.src=enemys[j].boomsrc;enemys[j].planisdie=true;scores+=enemys[j].score;label.innerHTML=scores;}
maindiv.removeChild(bullets[k].bulletnode);bullets.splice(k,1);bulletlen--;break;}}}}}
function plan(x,y,w,h,imgsrc,speed,boomsrc,hp,dietime,score){this.x=x;this.y=y;this.imgsrc=imgsrc;this.speed=speed;this.w=w;this.h=h;this.boomsrc=boomsrc;this.hp=hp;this.planisdie=false;this.dietime=dietime;this.diecount=0;this.score=score;this.plannode=null;this.init=function(){this.plannode=document.createElement("img");this.plannode.style.position="absolute";this.plannode.style.left=this.x+"px";this.plannode.style.top=this.y+"px";this.plannode.src=this.imgsrc;};this.init();maindiv.appendChild(this.plannode);this.move=function(){this.plannode.style.top=this.plannode.offsetTop+this.speed+"px";}}
function bullet(x,y,imgsrc){this.x=x;this.y=y;this.imgsrc=imgsrc;this.bulletnode=null;this.init=function(){this.bulletnode=document.createElement("img");this.bulletnode.style.position="absolute";this.bulletnode.style.left=x+"px";this.bulletnode.style.top=y+"px";this.bulletnode.src=this.imgsrc;maindiv.appendChild(this.bulletnode);}
this.init();this.move=function(){this.bulletnode.style.top=this.bulletnode.offsetTop-20+"px";}}
function selfpaln(x,y){plan.call(this,x,y,null,null,"image/我的飞机.gif",null,null,null,null,null);}
var ourmove=function(){var oevent=window.event||arguments[0];var selfplanX=oevent.clientX-500;var selfplanY=oevent.clientY;var x=oevent.clientX;var y=oevent.clientY;if(!(x<533||x>787||y<40||y>528)){selfpaln.plannode.style.left=selfplanX-33+"px";selfpaln.plannode.style.top=selfplanY-40+"px";}}
var borderline=function(){var oevent=window.event||arguments[0];var x=oevent.clientX;var y=oevent.clientY;if(x<533||x>787||y<40||y>528){if(document.removeEventListener){maindiv.removeEventListener("mousemove",ourmove,true);}else if(document.detachEvent){maindiv.detachEvent("onmousemove",ourmove);}}else{if(document.addEventListener){maindiv.addEventListener("mousemove",ourmove,true);}else if(document.attachEvent){maindiv.attachEvent("onmousemove",ourmove);}}}
var selfpaln=new selfpaln(120,485);function enemy(a,b,w,h,imgsrc,speed,boomsrc,hp,dietime,score){plan.call(this,random(a,b),50,w,h,imgsrc,speed,boomsrc,hp,dietime,score);}
function random(a,b){return Math.floor(Math.random()*(b-a)+a);}
function oddbullet(x,y){bullet.call(this,x,y,"image/bullet1.png");}
if(document.addEventListener){maindiv.addEventListener("mousemove",ourmove,true);}else if(document.attachEvent){maindiv.attachEvent("onmousemove",ourmove);}